'use strict';angular.module('blogcat', ['blogcatServices','ngResource', 'ngGrid']).  config(['$routeProvider', function($routeProvider) {  $routeProvider.      when('articles/indexJSON', {templateUrl: 'list.html',   controller: ListCtrl}).      when('articles/:id', {templateUrl: 'list.html',   controller: ListCtrl}).      otherwise({templateUrl: 'list.html',   controller: ListCtrl});}]);function ListCtrl($scope, Articles) {  $scope.articles = Articles.query();    $scope.gridOptions = {        data: 'articles',        multiSelect: false,        columnDefs: [{field: '_id', displayName: 'Id', visible: false }                    , {field: 'title', displayName: 'Title'}                    , {field:'body', displayName:'Body'}                    ,  { field: '', cellTemplate: '<button ng-click="edit(row)">Edit</button><button ng-click="delete(row)">Delete</button>' }        ]    };    $scope.delete = function(row) {        console.log("row.entity = " + row.entity);//        row.entity.$delete({id:row.entity._id})        row.entity.$delete({id:row.entity._id})            .then(function(response) {                if (response.status === 'OK') {                    remove($scope.gridData, '_id', row.entity._id);                }            });    };}// parse the gridData array to find the object with testIdfunction remove(array, property, value) {    $.each(array, function(index, result) {        if (result[property] == value) {            array.splice(index, 1);        }    });}